import React from 'react'
import './style/ListItems.css'
import List from './List';
function ListItems() {
    // const data = [

    //     { id: 1, task: "buy a tshirt" },

    //     { id: 2, task: "read newspaper and books" },

    //     { id: 3, task: "go for adventure" },
    //     { id: 1, task: "buy a tshirt" },

    //     { id: 2, task: "read newspaper and books" },

    //     { id: 3, task: "go for adventure" }
    //   ];

    let todoList=JSON.parse(localStorage.getItem('taskList'))
    return (
      <div className='user'>
        {todoList.map((d) => {
          return (
            <List title = {d.title} key={d.id} completed={d.completed} />
          );
        })}
      </div>
    );
  };

export default ListItems
